import React, { createContext, useReducer } from 'react';

// import appreducer
import AppReducer from './AppReducer';

// create a initial state
const initialState = {
    transactions: [
        {id: 1 , discription: "income" , transactionamount: 200},
        {id: 2 , discription: "expence" , transactionamount: -50},
        {id: 3 , discription: "income" , transactionamount: 100},
        {id: 4 , discription: "expence" , transactionamount: -25}
    ]
}

// create Glbal context
export const GlobalContext = createContext(initialState);

//create the provider for the global context
export const GlobalProvider = ({children}) => {

    const [state , dispatch] = useReducer(AppReducer , initialState)

    // Action
    function deleteTransaction(id) {
        dispatch({
            type : 'DELETE_TRANSACTION',
            payload : id
        });
    }

    return (
        <GlobalContext.Provider value={
            {
                transactions: state.transactions
            }
        }>
            {children}
        </GlobalContext.Provider>
    );
}
